import React, { useEffect, useState } from "react";
import CountryCard from "./CountryCard";
import { Form, Spinner } from "react-bootstrap";
import "./CountryList.css";

const CountryList = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [countries, setCountries] = useState([]);

  const handleQuery = (e) => {
    setSearchQuery(e.target.value);
  };

  const url = "https://restcountries.com/v3.1";

  async function fetchData() {
    try {
      let res = await fetch(`${url}/currency/${searchQuery.toLowerCase()}`);
      let data = await res.json();
     setCo
    } catch (isError) {
    }
  }

  useEffect(()=>{
    fetchData()
  },[searchQuery])

  const countriesList = () => {
    if (loading) {
      return <Spinner />;
    }
    if (countries?.length === 0) {
      return "No Results Found!";
    }
    if (countries?.length) {
      return countries?.map((country, i) => (
        <CountryCard key={i + 1} country={country} />
      ));
    }
  };

  return (
    <>
      <h1 className="heading">Currency App</h1>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Enter Currency code</Form.Label>
        <Form.Control
          type="text"
          placeholder="Search By currency INR, EUR"
          value={searchQuery}
          onChange={handleQuery}
        />
      </Form.Group>
      <div>{countriesList}</div>
    </>
  );
};

export default CountryList;
