import React, { useEffect, useState } from "react";
import CountryCard from "./CountryCard";
import { Container, Form, Spinner } from "react-bootstrap";
import "./CountryList.css";
import axios from "axios";

const CountryList = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [countries, setCountries] = useState([]);

  const handleQuery = (e) => {
    setSearchQuery(e.target.value);
  };

  const url = "https://restcountries.com/v3.1";

  // async function fetchData() {
  //   try {
  //     let res = await fetch(`${url}/currency/${searchQuery.toLowerCase()}`);
  //     let data = await res.json();
  //     setCountries(data);
  //   } catch (isError) {}
  // }

  const handleSearch = async () => {
    try {
      setLoading(true);
      const response = await axios.get(
        `${url}/currency/${searchQuery.toLowerCase()}`
      );
      console.log(response);
      setCountries(response.data);
    } catch (error) {
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (searchQuery.length > 0) {
      handleSearch();
    }
  }, [searchQuery]);

  const countriesList = () => {
    console.log(countries)
    // if (loading) {
    //   return <Spinner />;
    // }
    // if (countries?.length === 0) {
    //   return "No Results Found!";
    // }
    if (countries?.length>0) {
      return countries?.map((country, i) => {
        return (
          <div key={i + 1} className>
            <CountryCard country={country} />
          </div>
        );
    });
    }
  };

  return (
    <>
      <Container>
        <h1 className="heading mt-5">Currency App</h1>
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label>Enter Currency code</Form.Label>
          <Form.Control
            type="text"
            placeholder="Search By currency INR, EUR"
            value={searchQuery}
            onChange={handleQuery}
          />
        </Form.Group>
        <div>{countriesList()}</div>
      </Container>
    </>
  );
};

export default CountryList;
